mapper.select:
SELECT * FROM orders

    WHERE sellername like #{sellername} or buyername like #{sellername}

LIMIT #{pageStart},#{pageSize}

mapper.update:
update item
set amount=(amount-#{amount})
where item_name=#{item_name} and username=#{sellername}

mapper.select:
select amount-#{amount}
from item
where item_name=#{item_name} and username=#{sellername}
